import React, { useEffect, useState } from "react";
import "./App.css";
import loadScript from "./custom/loadScript";
import axios from "axios";
import StaticPage from "./StaticPage";

const Home = ({ navigation }) => {
  /**
   * Customer Details state
   */
  const [customerName, setCustomerName] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerAmount, setCustomerAmount] = useState("");
  // ----------------------------------------------------------------

  /**
   * Tracking Id and status state
   */
  const [trackId, setTrackId] = useState("");
  const [getStatus, setStatus] = useState("");
  const [throwTrackingId, setThrowTrackingId] = useState({
    condition: false,
    uuid: "",
  });
  // ----------------------------------------------------------------

  /**
   * Button State for actions
   */
  const [dataRecieved, setDataRecieved] = useState(false);
  const [btnClicked, setBtnClicked] = useState(false);
  const [btnDisabled, seBtnDisabled] = useState(false);
  const [formError, setFormError] = useState("");
  // ----------------------------------------------------------------
  //

  /**
   * status - failed || success
   * message - String
   * paymentDetails - Oject
   *          - name : String
   *          - email : String
   *          - phoneNumber : Number
   *          - amount : Number
   *          - razorpay_order_id : String
   *          - razorpay_payment_id : String
   *          - razorpay_signature : String
   *          - payment_date : Date
   */
  const [fetchPayment, setPaymentDetails] = useState({
    status: "",
    message: "",
    paymentDetails: {
      name: "",
      email: "",
      phoneNumber: "",
      amount: "",
      razorpay_order_id: "",
      razorpay_payment_id: "",
      razorpay_signature: "",
      payment_date: "",
    },
  });

  /**
   * - Snippet for Refreshing the details
   */
  const Refresh_details = () => {
    setCustomerName("");
    setCustomerPhone("");
    setCustomerEmail("");
    setCustomerAmount("");
  };

  /**
   * Razorpay Checkout for payment
   */
  const razorpayPopup = () => {
    if (
      customerName !== "" &&
      customerEmail !== "" &&
      customerPhone !== "" &&
      customerPhone.length === 10 &&
      customerAmount !== ""
    ) {
      seBtnDisabled(true);
      loadScript("https://checkout.razorpay.com/v1/checkout.js")
        .then((ok) => {
          // create order and receve the order_id for payment
          axios
            .post("/api/create/order", {
              amount: customerAmount * 100, // amount in the smallest currency unit => (INDIAN Currency) => 100paisa is Rs.1 (so you have to multiply the same ammount wiht 100)
              receipt: "order_rcptid_10", // you can automactically change if you can
            })
            .then((res) => {
              const { orderDetails } = res.data;
              const { amount, id, currency } = orderDetails; // Pulling the some data from the orderDetails

              var options = {
                key: "rzp_test_CQhYw03W2mfalF", // Enter the Key ID generated from the Dashboard
                amount: parseInt(amount), // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
                currency: currency,
                name: "Rishu Chowdhary",
                description: "Please donate thank you :)",
                image:
                  "https://raw.githubusercontent.com/hirishu10/my-assets/main/logo.png",
                order_id: id, //This is a sample Order ID. Pass the `id` obtained in the previous step
                handler: (response) => {
                  const datasend = {
                    name: customerName,
                    email: customerEmail,
                    amount: parseInt(amount),
                    phoneNumber: customerPhone,
                    razorpay_payment_id: response.razorpay_payment_id,
                    razorpay_order_id: response.razorpay_order_id,
                    razorpay_signature: response.razorpay_signature,
                  };
                  pushDataIntoDatabase(datasend); // send the data for adding in the database
                },
                prefill: {
                  name: customerName,
                  email: customerEmail,
                  contact: customerPhone,
                },
                notes: {
                  address: "Please visit https://github.com/hirishu10",
                },
                theme: {
                  color: "#3399cc",
                },
              };
              const paymentScreen = new window.Razorpay(options);
              paymentScreen.open();
              seBtnDisabled(false);
              Refresh_details();
            })
            .catch((err) => {
              alert("server error!");
              seBtnDisabled(false);
            });
        })
        .catch((err) => {
          alert(err);
          seBtnDisabled(false);
        });
    } else {
      seBtnDisabled(false);
      setFormError("Please fill the form carefully");
    }
  };

  /**
   * Connect the database for updating the data
   */
  const pushDataIntoDatabase = (datasend) => {
    axios
      .post("/api/create/order/paymentdetail/Save", datasend, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((item) => {
        setThrowTrackingId({
          condition: true,
          uuid: item?.data?.data?._id,
        });
        Refresh_details();
      })
      .catch((err) => {
        console.log("err :>> ", err);
      });
  };

  /**
   *
   * @param {*} trackingId - Tracking id which you received after successfully use (Try First)
   *
   * - Track Payment details from the server
   */
  const getPaymentDetails = (trackingId) => {
    axios
      .get("/api/trackpayment/details/:id", {
        headers: {
          "Content-Type": "application/json",
          id: trackingId,
        },
      })
      .then((item) => {
        setStatus(item?.data?.status);
        setPaymentDetails({
          status: item?.data?.status,
          message: item?.data?.message,
          paymentDetails: {
            name: item?.data?.paymentDetails?.name,
            email: item?.data?.paymentDetails?.email,
            phoneNumber: item?.data?.paymentDetails?.phoneNumber,
            amount: item?.data?.paymentDetails?.amount,
            razorpay_order_id: item?.data?.paymentDetails?.razorpay_order_id,
            razorpay_payment_id:
              item?.data?.paymentDetails?.razorpay_payment_id,
            razorpay_signature: item?.data?.paymentDetails?.razorpay_signature,
            payment_date: item?.data?.paymentDetails?.payment_date,
          },
        });
      })
      .catch((err) => {
        console.log("err :>> ", err);
      });
  };

  /**
   * useEffect helps to render the updated data
   */
  useEffect(() => {}, [btnClicked, dataRecieved]);

  /**
   * Main Function
   */
  return (
    <div>
      <header className="App-header">
        {/*
         *
         * - Throw tracking id after payment done
         *
         */}
        {throwTrackingId.condition ? (
          <div
            className="alert alert-warning alert-dismissible fade show"
            role="alert"
          >
            <div className="me-5" style={{ fontSize: 15 }}>
              <strong>{`Payment Tracking Id â†’   `}</strong>
              {`  ${throwTrackingId.uuid}`}
            </div>
            <button
              type="button"
              className="btn-close h-25"
              data-bs-dismiss="alert"
              aria-label="Close"
              onClick={(e) => {
                e.preventDefault();
                setThrowTrackingId({
                  condition: false,
                  uuid: "",
                });
              }}
            ></button>
          </div>
        ) : null}
        {/* ------ */}
        {/*
         *
         * - LOGO (Not mandatory)
         *
         */}
        <img
          src="https://raw.githubusercontent.com/hirishu10/my-assets/main/top_log.png"
          width={100}
          height={70}
          alt={"logo"}
          className="logo-design"
        />
        {/* ------- */}

        {/*
         *
         * - First Try and QR-Code
         *
         */}
        <div style={{ display: "flex" }}>
          <div className="btn-design-div me-3">
            <p>
              Payment Page with <code>React</code>
            </p>
            <button
              className="btn-design btn btn-outline-success fw-bold"
              title="Donate"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#userDetails"
              onClick={(e) => {
                e.preventDefault();
                setThrowTrackingId({
                  condition: false,
                  uuid: "",
                });
              }}
            >
              Try First
              {btnDisabled ? (
                <div
                  className="spinner-border spinner-border-sm ms-1"
                  role="status"
                >
                  <span className="sr-only"></span>
                </div>
              ) : null}
            </button>
            {/*
             *
             * - User modal for taking the input
             *
             */}
            <div
              className="modal fade"
              id="userDetails"
              tabIndex="-1"
              aria-labelledby="userDetailsLabel"
              aria-hidden="true"
            >
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header bg-primary">
                    <h5 className="modal-title" id="userDetailsLabel">
                      User Details
                    </h5>
                    <button
                      type="button"
                      className="btn-close"
                      data-bs-dismiss="modal"
                      aria-label="Close"
                      onClick={(e) => {
                        e.preventDefault();
                        Refresh_details();
                        setFormError("");
                      }}
                    ></button>
                  </div>
                  <div className="modal-body">
                    {/* full name */}
                    <div className="input-group mb-3">
                      <span className="input-group-text">Full Name</span>
                      <input
                        type="text"
                        aria-label="Full name"
                        className="form-control"
                        value={customerName}
                        onChange={(e) => {
                          e.preventDefault();
                          setCustomerName(e.target.value);
                          setFormError("");
                        }}
                      />
                    </div>
                    {/* email */}
                    <div className="input-group mb-3">
                      <span className="input-group-text">Email Address</span>
                      <input
                        type="text"
                        aria-label="Email"
                        className="form-control"
                        value={customerEmail}
                        onChange={(e) => {
                          e.preventDefault();
                          setCustomerEmail(e.target.value);
                          setFormError("");
                        }}
                      />
                    </div>
                    {/* phone */}
                    <div className="input-group mb-3">
                      <span className="input-group-text">Phone Number</span>
                      <input
                        type="number"
                        maxLength={10}
                        aria-label="Phone"
                        className="form-control"
                        value={customerPhone}
                        onChange={(e) => {
                          e.preventDefault();
                          setCustomerPhone(e.target.value);
                          setFormError("");
                        }}
                      />
                    </div>
                    {/* amount */}
                    <div className="input-group mb-3">
                      <span className="input-group-text">Amount â‚¹</span>
                      <input
                        type="number"
                        aria-label="Phone"
                        className="form-control"
                        value={customerAmount}
                        onChange={(e) => {
                          e.preventDefault();
                          setCustomerAmount(e.target.value);
                          setFormError("");
                        }}
                      />
                    </div>
                    {/**************************************************/}
                  </div>
                  <p className="bg-danger text-light">{formError}</p>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      data-bs-dismiss="modal"
                      onClick={(e) => {
                        e.preventDefault();
                        Refresh_details();
                        setFormError("");
                      }}
                    >
                      Close
                    </button>
                    <button
                      type="button"
                      className="btn btn-primary"
                      onClick={razorpayPopup}
                      disabled={btnDisabled}
                      data-bs-dismiss={
                        customerName !== "" &&
                        customerEmail !== "" &&
                        customerPhone !== "" &&
                        customerPhone.length === 10 &&
                        customerAmount !== ""
                          ? "modal"
                          : ""
                      }
                    >
                      Submit
                    </button>
                  </div>
                </div>
              </div>
            </div>
            {/*
             *
             * - User modal end
             *
             */}
          </div>
          {/*
           *
           * - QR-Code Button
           *
           */}
          {/* Button trigger for open QR */}
          <button
            type="button"
            className="btn btn-outline-success"
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"
          >
            Click to Scan QR
          </button>
          {/* Modal showing the QR */}
          <div
            className="modal fade"
            id="exampleModal"
