        >
          Track Payment Details
        </button>
        {/* Track payment Modal  */}
        <div
          className="modal fade"
          id="paymentDetails"
          tabIndex="-1"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header bg-warning text-dark">
                <h5 className="modal-title" id="exampleModalLabel">
                  Payment Details (Try First Database)
                </h5>
                <button
                  type="button"
                  className="btn-close btn-light"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                  onClick={(e) => {
                    e.preventDefault();
                    setDataRecieved(false);
                    setTrackId("");
                  }}
                ></button>
              </div>
              <div className="modal-body modal-body-custom">
                {/* top */}
                <div className="b-one">
                  <div className="input-group">
                    {/* Find by ID */}
                    <div className="input-group-text">Enter Track Id :</div>
                    <input
                      type="text"
                      className="form-control"
                      id="inlineTrackingId"
                      placeholder="Enter Tracking Number"
                      value={trackId}
                      onChange={(e) => {
                        e.preventDefault();
                        setTrackId(e.target.value);
                      }}
                    />
                    <button
                      type="submit"
                      className="btn btn-primary track-button"
                      onClick={(e) => {
                        e.preventDefault();
                        if (trackId !== "") {
                          setDataRecieved(false);
                          setBtnClicked(true);
                          getPaymentDetails(trackId);
                          setTimeout(() => {
                            setBtnClicked(false);
                            setDataRecieved(true);
                          }, 2000);
                        } else {
                          alert("Please enter valid Tracking Id");
                        }
                      }}
                      disabled={btnClicked}
